  `fid` int(11) NOT NULL COMMENT '运费模板',
  `supplier_id` int(11) NOT NULL DEFAULT '0' COMMENT '供应商ID',
  `shop_id` int(11) NOT NULL,
  `update_time` int(11) NOT NULL,
  `create_time` int(11) NOT NULL,
