-- ----------------------------
-- Procedure structure for kanJiang
-- ----------------------------
DROP PROCEDURE IF EXISTS `kanJiang`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `kanJiang`(`_betId` INT, `_zjCount` INT, `_kjData` VARCHAR(255) CHARACTER SET utf8, `_kset` VARCHAR(255) CHARACTER SET utf8)
begin
	
	declare `uid` int;									
	declare parentId int;								
	declare username varchar(32) character set utf8;	
	
	
	declare actionNum int;
	declare serializeId varchar(64);
	declare actionData longtext character set utf8;
	declare actionNo varchar(255);
	declare `type` int;
	declare playedId int;
	
	declare isDelete int;
	
	declare fanDian float;		
	declare `mode` float;		
	declare beiShu int;			
	declare zhuiHao int;		
	declare zhuiHaoMode int;	
	declare bonusProp float;	
	
	declare amount float;					
	declare zjAmount float default 0;		
	declare _fanDianAmount float default 0;	
	
	declare liqType int;
	declare info varchar(255) character set utf8;
	
	declare _parentId int;		
	declare _fanDian float;		

	
	declare done int default 0;
	declare cur cursor for
	select b.`uid`, u.parentId, u.username, b.actionNum, b.serializeId, b.actionData, b.actionNo, b.`type`, b.playedId, b.isDelete, b.fanDian, u.fanDian, b.`mode`, b.beiShu, b.zhuiHao, b.zhuiHaoMode, b.bonusProp, b.actionNum*b.`mode`*b.beiShu amount from blast_bets b, blast_members u where b.`uid`=u.`uid` and b.id=_betId;
	declare continue handler for sqlstate '02000' set done = 1;
	
	open cur;
		repeat
			fetch cur into `uid`, parentId, username, actionNum, serializeId, actionData, actionNo, `type`, playedId, isDelete, fanDian, _fanDian, `mode`, beiShu, zhuiHao, zhuiHaoMode, bonusProp, amount;
		until done end repeat;
	close cur;
	
	

	
	start transaction;
	-- if md5(_kset)='47df5dd3fc251a6115761119c90b964a' then
	
		
		if isDelete=0 then
			
			
			call addScore(`uid`, amount);

		   if type<>34 then
		
			
			 if fanDian then
				 set liqType=2;
				 set info='返点';
				 set _fanDianAmount=amount * fanDian/1000;
				 call setCoin(_fanDianAmount, 0, `uid`, liqType, `type`, info, _betId, '', '');
			 end if;
			
			
			set _parentId=parentId;
			
			set fanDian=_fanDian;
			
			while _parentId do
				call setUpFanDian(amount, _fanDian, _parentId, `type`, _betId, `uid`, username);
			end while;
			set _fanDianAmount = _fanDianAmount + amount * ( _fanDian - fanDian)/100;
			

		   end if;

			
			if _zjCount then
				

				set liqType=6;
				set info='中奖奖金';

				if type=34 then
				    set zjAmount=bonusProp * _zjCount * beiShu;
				else
				    set zjAmount=bonusProp * _zjCount * beiShu * `mode`/2;
				end if;

				call setCoin(zjAmount, 0, `uid`, liqType, `type`, info, _betId, '', '');
	
			end if;
			
			
			update blast_bets set lotteryNo=_kjData, zjCount=_zjCount, bonus=zjAmount, fanDianAmount=_fanDianAmount where id=_betId;

			
			if _zjCount and zhuiHao=1 and zhuiHaoMode=1 then
				
				
				
				call cancelBet(serializeId);
			end if;
		end if;
	-- end if;
	
	commit;
	
end
;;
DELIMITER ;

